Item.method_code:
var inn = this.getInnovator();
var qd_id = this.getID();
//"467143F3250D42CF9D2F6F880ABD3999"; // for testing

var convert = inn.applyMethod("QD2RDFXML","<qd_id>" + qd_id + "</qd_id>");

if (convert.isError())
    return alert(convert.getErrorString());
    
var nodeResult = convert.toString();

nodeResult = nodeResult.replace('xmlns:SOAP-ENV=', "");
nodeResult = nodeResult.replace('"http://schemas.xmlsoap.org/soap/envelope/" ', "");
nodeResult = nodeResult.replace('<SOAP-ENV:Envelope xmlns:i18n="http://www.aras.com/I18N"><SOAP-ENV:Body><Result>', "");
nodeResult = nodeResult.replace('</Result></SOAP-ENV:Body></SOAP-ENV:Envelope>', "");

return nodeResult;
