Item.method_code:
// parameters: 
// path - filepath on server. should end in backslash.
// filename - file name with extension
// content - string to write to file

Innovator inn = this.getInnovator();
string path = this.getProperty("path","");
string filename = this.getProperty("filename","");
string content = this.getProperty("content","");

if (path == "" || filename == "" || content == "")
    return inn.newError("Must define all parameters: path, filename, content");

System.IO.File.WriteAllText(path + filename, content);

return inn.newResult("Success");
